create table posts
(
    post_id         integer     not null
        constraint post_pkey
            primary key,
    title           varchar     not null,
    content         varchar
        unique,
    posting_time    timestamp,
    posting_city    varchar,
    posting_country varchar,
    author_id       varchar(18) not null
        constraint fk_author
            references authors,
    user_id         integer
        unique
        constraint fk_user
            references users
);

alter table posts
    owner to postgres;

