create table secondary_replies
(
    sec_reply_id      serial
        constraint sec_reply_pkey
            primary key,
    reply_id          integer not null
        constraint fk_reply
            references replies,
    sec_reply_content varchar not null,
    sec_reply_starts  integer not null,
    sec_reply_userid  integer not null
        constraint fk_author
            references users
);

alter table secondary_replies
    owner to postgres;

