INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1843, 753, 'Thanks! During the first few miles, I was really nervous and excited. As the race went on, I started to feel more tired, but I pushed through and it was worth it in the end.', 9, 8989);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1844, 753, 'I bet it was really challenging. How did you manage to stay motivated throughout the race?', 9, 7657);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1845, 754, 'I followed a strict training schedule for months leading up to the race, which included long runs on weekends and shorter runs during the week. I also focused on eating healthy and getting enough rest.', 5, 805);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1846, 754, 'Did you have any setbacks during your training?', 18, 3720);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1847, 755, 'You should definitely give it a try! Start by building up your endurance and finding a training plan that works for you. And most importantly, believe in yourself and stay positive throughout the process.', 6, 8834);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1848, 755, 'I''m not sure if I have the discipline to train for a marathon. How did you stay motivated during your training?', 12, 82);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1849, 756, 'I think anyone can do it if they are willing to put in the time and effort to train properly. It''s all about setting a goal and working towards it.', 4, 8990);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1850, 756, 'Do you plan on running another marathon in the future?', 18, 8991);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1851, 757, 'Definitely! It has helped me become more disciplined and motivated in other areas of my life. Plus, it''s a great way to stay in shape and improve your overall health.', 1, 8395);
