INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1853, 758, 'It took a bit of time, but I slowly got used to it as I climbed higher. I also had a great instructor who was very patient and helped me feel more confident.', 7, 8887);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1854, 758, 'I was also scared of heights at first, but the more I climbed, the more I got used to it. I think having a supportive group of friends with me also helped a lot.', 4, 2877);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1855, 759, 'I rented gear at the climbing gym where I went. They had everything I needed, including climbing shoes, harness, and a chalk bag.', 12, 4946);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1856, 759, 'I invested in my own gear since I plan to do this more often. I got a harness, climbing shoes, chalk bag, and a helmet. It can be a bit pricey, but it''s worth it for safety and comfort.', 11, 1339);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1857, 760, 'I love the sense of accomplishment I get when I reach the top of a difficult climb.', 6, 1959);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1858, 760, 'For me, it''s the mental and physical challenge. Each climb is like solving a puzzle and it''s really satisfying to figure it out.', 7, 7173);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1859, 760, 'I like the social aspect too. It''s a great way to meet new people who share the same interest.', 4, 8248);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1860, 761, 'Take it slow and don''t push yourself too hard. Start with easier climbs and work your way up.', 8, 144);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (1861, 761, 'Don''t be afraid to ask for help or advice from more experienced climbers. They''re usually happy to give tips and pointers.', 6, 7221);
