INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2523, 1020, 'Thank you! It was a full marathon, 42.195 kilometers. I trained for three months prior to the marathon and gradually increased my distance every week.', 0, 2950);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2524, 1020, 'It was a half marathon, 21.0975 kilometers. I followed a strict training plan and tried to maintain a healthy diet.', 3, 7163);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2525, 1021, 'I felt really nervous at the beginning, but as the race progressed, I started to enjoy it more. There were times when I felt exhausted and wanted to give up, but the cheers from the crowd and my own determination kept me going.', 6, 2270);
