INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2527, 1022, 'Yes, I tried to maintain a steady pace throughout the race and not start too fast. I also made sure to drink enough water and take energy gels at regular intervals to keep my energy levels up.', 6, 3820);
