INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2532, 1024, 'I had some trouble with my breathing at one point, but I slowed down and took deep breaths to help me recover.', 3, 8803);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2533, 1025, 'Start training early and gradually increase your distance. Don''t push yourself too hard too quickly. Also, make sure to take care of your body by eating well and getting enough rest.', 20, 8042);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2534, 1025, 'Stay motivated and believe in yourself. The journey to the finish line is not easy, but it''s definitely worth it.', 10, 9095);
INSERT INTO public.secondary_replies (sec_reply_id, reply_id, sec_reply_content, sec_reply_starts, sec_reply_userid) VALUES (2535, 1026, 'The best part was definitely crossing the finish line and knowing that I had accomplished something that I never thought was possible. The sense of achievement was amazing.', 5, 9096);
